// Dessiner le héro
void paintHero(Character &aCharacter) {
  int x = aCharacter.x - OVER_CENTER_X_HERO;
  int y = aCharacter.y - OVER_CENTER_Y_HERO;
  if(aCharacter.toTheLeft) {
    // le personnage se déplace vers la gauche

    if(aCharacter.animation % 2 == 0) {
      gb.display.drawImage(x, y, heroG1);
    } else {
      gb.display.drawImage(x, y, heroG2);
    }
    
  } else {
    // le personnage se déplace vers la droite

    if(aCharacter.animation % 2 == 0) {
      gb.display.drawImage(x, y, heroD1);
    } else {
      gb.display.drawImage(x, y, heroD2);
    }
    
  }
  // debug
  //gb.display.printf("%d,%d : %d", x, y, aCharacter.animation); // <<<<<<<<<<<<<<<<<<<<<<<<<<<<<
}

// Dessiner toutes les plateformes
void paintPlatforms(Platform * aSet) {
  for(int i = 0 ; i < NB_OF_PLATFORMS ; i++) {
    paintPlatform(aSet[i]);
  }
}

// Dessiner une plateforme
void paintPlatform(Platform aPlatform) {
  int x = aPlatform.x - OVER_CENTER_X_PLATFORM;
  int y = aPlatform.y - OVER_CENTER_Y_PLATFORM;
  
  for(int i = 0 ; i < aPlatform.lengthPlatform ; i++) {
    
    if(i == 0) {
      // si c'est le début de la plateforme
      // alors on dessine une plateforme droite
      gb.display.drawImage(x, y, platformRight);
    } else if(i == (aPlatform.lengthPlatform - 1)) {
      // si c'est la fin de la plateforme
      // alors on dessine une plateforme gauche
      gb.display.drawImage(x, y, platformLeft);
    } else {
      // sinon on dessine une plateforme centrale
      gb.display.drawImage(x, y, platformMiddle);
    }

    x += WIDTH_PLATFORM;
  }
}
